package day12;


public class K_MathEx01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// abs() 절댓값 예제.
		System.out.println("-1의 절댓값 : " + Math.abs(-1));

		// ceil() 첫번째 자리에서 올림 
		System.out.println("1.23을 소수점 첫번째 자리에서 올림 : " + Math.ceil(1.23));
		
		// floor() 첫번째 자리에서 버림 
		System.out.println("1.23을 소수점 첫번째 자리에서 버림 : " + Math.floor(1.23));
		
		// round() 반올림 후 정수를 리턴 
		System.out.println("1.23을 소수점 첫번째 자리에서 반올림 후 정수 리턴 : " + Math.round(1.23));
		
		// sqrt() : 루트 값
		// pow(num,n) : n제곱
		
	}

}
